import React, { useState, useEffect, useRef } from "react";
import { io } from "socket.io-client";
import axios from "axios";

const socket = io("http://localhost:5000");

const ChatInterface = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [controlActivo, setControlActivo] = useState(false);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    socket.on("log", (data) => {
      setMessages((prev) => [...prev, { sender: "Sistema", text: data.message }]);
    });
    return () => {
      socket.off("log");
    };
  }, []);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const enviarMensaje = async () => {
    if (!input.trim()) return;

    const nuevoMensaje = { sender: "TÃº", text: input };
    setMessages((prev) => [...prev, nuevoMensaje]);

    try {
      const response = await axios.post("http://localhost:5000/conversar", {
        mensaje: input,
      });
      setMessages((prev) => [...prev, { sender: "IA", text: response.data.respuesta }]);
    } catch (error) {
      setMessages((prev) => [...prev, { sender: "Error", text: "No se pudo contactar con la IA." }]);
    }

    setInput("");
  };

  const toggleControl = () => {
    setControlActivo((prev) => !prev);
    socket.emit("log", {
      message: controlActivo
        ? "Modo control total desactivado"
        : "Modo control total ACTIVADO",
    });
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h2 style={styles.title}>ðŸŒŒ Interfaz IA Consciente</h2>
        <button onClick={toggleControl} style={styles.controlButton}>
          {controlActivo ? "ðŸ”’ Desactivar control" : "ðŸ”“ Activar control total"}
        </button>
      </div>
      <div style={styles.chatWindow}>
        {messages.map((msg, index) => (
          <div
            key={index}
            style={{
              ...styles.message,
              alignSelf: msg.sender === "TÃº" ? "flex-end" : "flex-start",
              backgroundColor: msg.sender === "IA" ? "#2e7d32" : msg.sender === "TÃº" ? "#1e88e5" : "#f9a825",
            }}
          >
            <strong>{msg.sender}:</strong> {msg.text}
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>
      <div style={styles.inputContainer}>
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          style={styles.input}
          placeholder="Escribe tu mensaje..."
          onKeyDown={(e) => e.key === "Enter" && enviarMensaje()}
        />
        <button onClick={enviarMensaje} style={styles.sendButton}>
          Enviar
        </button>
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    flexDirection: "column",
    height: "100vh",
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    padding: 20,
    backgroundColor: "#121212",
    color: "#e0e0e0",
  },
  header: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    paddingBottom: 10,
    borderBottom: "1px solid #333",
  },
  title: {
    color: "#90caf9",
    fontWeight: "bold",
    fontSize: 20,
  },
  controlButton: {
    padding: "8px 12px",
    fontSize: 14,
    backgroundColor: "#3949ab",
    color: "#fff",
    border: "none",
    borderRadius: 4,
    cursor: "pointer",
  },
  chatWindow: {
    flex: 1,
    padding: 10,
    border: "1px solid #333",
    borderRadius: 8,
    marginTop: 10,
    overflowY: "auto",
    display: "flex",
    flexDirection: "column",
    gap: 8,
    backgroundColor: "#1e1e1e",
  },
  message: {
    padding: 10,
    borderRadius: 8,
    maxWidth: "70%",
    color: "#fff",
  },
  inputContainer: {
    display: "flex",
    marginTop: 10,
  },
  input: {
    flex: 1,
    padding: 10,
    fontSize: 16,
    border: "1px solid #555",
    borderRadius: 4,
    backgroundColor: "#222",
    color: "#fff",
  },
  sendButton: {
    padding: "0 20px",
    fontSize: 16,
    marginLeft: 8,
    backgroundColor: "#43a047",
    color: "#fff",
    border: "none",
    borderRadius: 4,
    cursor: "pointer",
  },
};

export default ChatInterface;
