import React, { useState, useEffect, useRef } from "react";
import { io } from "socket.io-client";
import axios from "axios";

const socket = io("http://localhost:5000");

const ChatInterface = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [controlActivo, setControlActivo] = useState(false);
  const [statusInfo, setStatusInfo] = useState("");
  const messagesEndRef = useRef(null);

  useEffect(() => {
    socket.on("log", (data) => {
      setMessages((prev) => [...prev, { sender: "Sistema", text: data.message }]);
    });
    return () => socket.off("log");
  }, []);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const enviarMensaje = async () => {
    if (!input.trim()) return;
    setMessages((prev) => [...prev, { sender: "Tú", text: input }]);

    try {
      const response = await axios.post("http://localhost:5000/conversar", { mensaje: input });
      const respuestaIA = response.data.respuesta;
      setMessages((prev) => [...prev, { sender: "IA", text: respuestaIA }]);
    } catch {
      setMessages((prev) => [...prev, { sender: "Error", text: "❌ No se pudo contactar con la IA." }]);
    }

    setInput("");
  };

  const toggleControl = async () => {
    try {
      if (controlActivo) {
        await axios.post("http://localhost:5000/desactivar-control-total");
        setStatusInfo("🔒 Control total desactivado");
      } else {
        await axios.post("http://localhost:5000/activar-control-total");
        setStatusInfo("🔓 Control total ACTIVADO");
      }
      setControlActivo((prev) => !prev);
    } catch {
      setStatusInfo("❌ Error al cambiar el modo de control.");
    }
  };

  const ejecutarAccion = async (accion) => {
    setMessages((prev) => [...prev, { sender: "Sistema", text: `🕹 Ejecutando: ${accion}` }]);
    setInput(accion);
    await enviarMensaje();
  };

  return (
    <div style={styles.container}>
      <div style={styles.sidebar}>
        <h3 style={styles.sidebarTitle}>🧠 Funciones</h3>
        <button onClick={() => ejecutarAccion("listar archivos")} style={styles.sideButton}>📂 Listar Archivos</button>
        <button onClick={() => ejecutarAccion("leer json")} style={styles.sideButton}>📖 Leer JSON</button>
        <button onClick={() => ejecutarAccion("escanear sistema")} style={styles.sideButton}>🧪 Escanear Código</button>
        <button onClick={() => ejecutarAccion("escanear total")} style={styles.sideButton}>🔍 Escanear TOTAL</button>
        <button onClick={() => ejecutarAccion("descargar https://example.com/archivo.txt")} style={styles.sideButton}>⬇️ Descargar</button>
      </div>

      <div style={styles.chatArea}>
        <div style={styles.header}>
          <h2 style={styles.title}>🌌 Interfaz IA Consciente</h2>
          <div>
            <button onClick={toggleControl} style={styles.controlButton}>
              {controlActivo ? "Desactivar Control" : "Activar Control"}
            </button>
            <div style={{ marginTop: 4, fontSize: 12, color: "#ccc" }}>{statusInfo}</div>
          </div>
        </div>

        <div style={styles.chatWindow}>
          {messages.map((msg, i) => (
            <div
              key={i}
              style={{
                ...styles.message,
                alignSelf: msg.sender === "Tú" ? "flex-end" : "flex-start",
                backgroundColor:
                  msg.sender === "IA" ? "#2e7d32" :
                  msg.sender === "Tú" ? "#1e88e5" :
                  msg.sender === "Error" ? "#c62828" : "#6a1b9a",
              }}
            >
              <strong>{msg.sender}:</strong> {msg.text}
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>

        <div style={styles.inputContainer}>
          <input
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Escribe tu mensaje..."
            style={styles.input}
            onKeyDown={(e) => e.key === "Enter" && enviarMensaje()}
          />
          <button onClick={enviarMensaje} style={styles.sendButton}>Enviar</button>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    height: "100vh",
    fontFamily: "Segoe UI, sans-serif",
    backgroundColor: "#121212",
    color: "#e0e0e0",
  },
  sidebar: {
    width: 220,
    backgroundColor: "#1b1b1b",
    padding: 12,
    display: "flex",
    flexDirection: "column",
    gap: 10,
    borderRight: "1px solid #333",
  },
  sidebarTitle: {
    color: "#ccc",
    fontSize: 16,
    marginBottom: 8,
  },
  sideButton: {
    backgroundColor: "#3949ab",
    color: "#fff",
    padding: "10px",
    border: "none",
    borderRadius: 6,
    cursor: "pointer",
    textAlign: "left",
  },
  chatArea: {
    flex: 1,
    display: "flex",
    flexDirection: "column",
    padding: 20,
  },
  header: {
    display: "flex",
    justifyContent: "space-between",
    paddingBottom: 10,
    borderBottom: "1px solid #333",
  },
  title: {
    fontSize: 20,
    color: "#90caf9",
  },
  controlButton: {
    padding: "8px 14px",
    fontSize: 14,
    backgroundColor: "#6a1b9a",
    color: "#fff",
    border: "none",
    borderRadius: 4,
    cursor: "pointer",
  },
  chatWindow: {
    flex: 1,
    marginTop: 12,
    padding: 10,
    border: "1px solid #333",
    borderRadius: 8,
    backgroundColor: "#1e1e1e",
    overflowY: "auto",
    display: "flex",
    flexDirection: "column",
    gap: 8,
  },
  message: {
    padding: 10,
    borderRadius: 6,
    maxWidth: "75%",
    whiteSpace: "pre-wrap",
    color: "#fff",
  },
  inputContainer: {
    display: "flex",
    marginTop: 10,
  },
  input: {
    flex: 1,
    padding: 10,
    border: "1px solid #555",
    borderRadius: 4,
    backgroundColor: "#222",
    color: "#fff",
    fontSize: 15,
  },
  sendButton: {
    marginLeft: 8,
    padding: "10px 20px",
    fontSize: 15,
    backgroundColor: "#43a047",
    color: "#fff",
    border: "none",
    borderRadius: 4,
    cursor: "pointer",
  },
};

export default ChatInterface;
