document.getElementById("btn-escanear-total").addEventListener("click", async () => {
    try {
        const res = await fetch("http://localhost:5000/utilidades/escanear-total");
        const data = await res.json();

        if (data.cambios_detectados) {
            alert("✅ Cambios detectados y aprendidos.");
        } else {
            alert("ℹ️ Sin cambios nuevos en el sistema.");
        }

        console.log("Resultado del escaneo total:", data);
    } catch (error) {
        alert("❌ Error al escanear el sistema.");
        console.error(error);
    }
});
