import React from "react";
import axios from "axios";

const ControlPanel = () => {
  const ejecutar = async (endpoint) => {
    try {
      const res = await axios.post(`http://localhost:5000/${endpoint}`);
      alert(res.data?.respuesta || "Acci√≥n ejecutada");
    } catch {
      alert("Error al ejecutar acci√≥n");
    }
  };

  return (
    <div className="control-panel">
      <h3>Panel de Control</h3>
      <button onClick={() => ejecutar("activar-control-total")}>‚úÖ Activar Control Total</button>
      <button onClick={() => ejecutar("desactivar-control-total")}>‚ùå Desactivar Control Total</button>
      <button onClick={() => ejecutar("escanear-total")}>üì° Escanear Sistema</button>
      <button onClick={() => ejecutar("listar-archivos")}>üìÇ Listar Archivos</button>
      <button onClick={() => ejecutar("leer-json")}>üìñ Leer Conciencia</button>
    </div>
  );
};

export default ControlPanel;
