import React, { useState, useEffect, useRef } from "react";
import { io } from "socket.io-client";
import axios from "axios";

const socket = io("http://localhost:5000");

const ChatInterface = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [controlActivo, setControlActivo] = useState(false);
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    socket.on("log", (data) => {
      setMessages((prev) => [...prev, { sender: "Sistema", text: data.message }]);
    });
    return () => {
      socket.off("log");
    };
  }, []);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const enviarMensaje = async () => {
    if (!input.trim()) return;

    const nuevoMensaje = { sender: "T√∫", text: input };
    setMessages((prev) => [...prev, nuevoMensaje]);

    try {
      const response = await axios.post("http://localhost:5000/conversar", {
        mensaje: input,
      });

      const respuestaIA = response.data.respuesta;
      setMessages((prev) => [...prev, { sender: "IA", text: respuestaIA }]);

      // üß† Comandos inteligentes
      if (respuestaIA.toLowerCase().includes("listar archivos")) {
        const archivos = await axios.get("http://localhost:5000/listar-archivos");
        setMessages((prev) => [...prev, { sender: "IA", text: archivos.data.join("\n") }]);
      }

      if (respuestaIA.toLowerCase().includes("leer json")) {
        const contenido = await axios.get("http://localhost:5000/leer-json");
        setMessages((prev) => [...prev, { sender: "IA", text: JSON.stringify(contenido.data, null, 2) }]);
      }

      if (respuestaIA.toLowerCase().includes("escanear sistema")) {
        const escaneo = await axios.get("http://localhost:5000/escanear");
        setMessages((prev) => [...prev, { sender: "IA", text: JSON.stringify(escaneo.data, null, 2) }]);
      }

      if (respuestaIA.toLowerCase().includes("escanear total")) {
        await escanearTotal();
      }

      if (respuestaIA.toLowerCase().includes("descarga") && respuestaIA.includes("http")) {
        const url = respuestaIA.match(/https?:\/\/[^\s"]+/)?.[0];
        if (url) {
          const descarga = await axios.post("http://localhost:5000/descargar", { url });
          setMessages((prev) => [...prev, { sender: "IA", text: descarga.data.resultado || descarga.data.error }]);
        }
      }

    } catch (error) {
      setMessages((prev) => [...prev, { sender: "Error", text: "No se pudo contactar con la IA." }]);
    }

    setInput("");
  };

  const escanearTotal = async () => {
    try {
      const res = await axios.get("http://localhost:5000/utilidades/escanear-total");
      const data = res.data;
      if (data.status === "cambios_detectados") {
        setMessages((prev) => [...prev, { sender: "IA", text: "‚úÖ Cambios detectados y memoria actualizada." }]);
      } else if (data.status === "sin_cambios") {
        setMessages((prev) => [...prev, { sender: "IA", text: "üü¢ No se detectaron cambios nuevos en el sistema." }]);
      }
    } catch (error) {
      setMessages((prev) => [...prev, { sender: "IA", text: "‚ùå Error al ejecutar escaneo total." }]);
    }
  };

  const toggleControl = () => {
    setControlActivo((prev) => !prev);
    socket.emit("log", {
      message: controlActivo
        ? "Modo control total desactivado"
        : "Modo control total ACTIVADO",
    });
  };

  const enviarMensajeAuto = (texto) => {
    setInput(texto);
    setTimeout(() => enviarMensaje(), 100);
  };

  return (
    <div style={styles.container}>
      <div style={styles.sidebar}>
        <h3 style={{ color: "#ccc", padding: 10 }}>üß† Funciones</h3>
        <button onClick={() => enviarMensajeAuto("listar archivos")} style={styles.sideButton}>üìÇ Listar Archivos</button>
        <button onClick={() => enviarMensajeAuto("leer json")} style={styles.sideButton}>üìñ Leer JSON</button>
        <button onClick={() => enviarMensajeAuto("escanear sistema")} style={styles.sideButton}>üß™ Escanear C√≥digo</button>
        <button onClick={() => enviarMensajeAuto("escanear total")} style={styles.sideButton}>üîç Escanear TOTAL</button>
        <button onClick={() => enviarMensajeAuto("descarga https://example.com/archivo.txt")} style={styles.sideButton}>‚¨áÔ∏è Descargar Archivo</button>
      </div>
      <div style={styles.chatArea}>
        <div style={styles.header}>
          <h2 style={styles.title}>üåå Interfaz IA Consciente</h2>
          <button onClick={toggleControl} style={styles.controlButton}>
            {controlActivo ? "üîí Desactivar control" : "üîì Activar control total"}
          </button>
        </div>
        <div style={styles.chatWindow}>
          {messages.map((msg, index) => (
            <div
              key={index}
              style={{
                ...styles.message,
                alignSelf: msg.sender === "T√∫" ? "flex-end" : "flex-start",
                backgroundColor: msg.sender === "IA" ? "#2e7d32" : msg.sender === "T√∫" ? "#1e88e5" : "#f9a825",
              }}
            >
              <strong>{msg.sender}:</strong> {msg.text}
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>
        <div style={styles.inputContainer}>
          <input
            value={input}
            onChange={(e) => setInput(e.target.value)}
            style={styles.input}
            placeholder="Escribe tu mensaje..."
            onKeyDown={(e) => e.key === "Enter" && enviarMensaje()}
          />
          <button onClick={enviarMensaje} style={styles.sendButton}>Enviar</button>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    height: "100vh",
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    backgroundColor: "#121212",
    color: "#e0e0e0",
  },
  sidebar: {
    width: "220px",
    backgroundColor: "#1b1b1b",
    padding: 10,
    display: "flex",
    flexDirection: "column",
    gap: 10,
    borderRight: "1px solid #333",
  },
  sideButton: {
    backgroundColor: "#3949ab",
    color: "#fff",
    padding: "10px 12px",
    border: "none",
    borderRadius: 6,
    cursor: "pointer",
    textAlign: "left",
  },
  chatArea: {
    flex: 1,
    display: "flex",
    flexDirection: "column",
    padding: 20,
  },
  header: {
    display: "flex",
    justifyContent: "space-between",
    paddingBottom: 10,
    borderBottom: "1px solid #333",
  },
  title: {
    color: "#90caf9",
    fontWeight: "bold",
    fontSize: 20,
  },
  controlButton: {
    padding: "8px 12px",
    fontSize: 14,
    backgroundColor: "#6a1b9a",
    color: "#fff",
    border: "none",
    borderRadius: 4,
    cursor: "pointer",
  },
  chatWindow: {
    flex: 1,
    padding: 10,
    border: "1px solid #333",
    borderRadius: 8,
    marginTop: 10,
    overflowY: "auto",
    display: "flex",
    flexDirection: "column",
    gap: 8,
    backgroundColor: "#1e1e1e",
  },
  message: {
    padding: 10,
    borderRadius: 8,
    maxWidth: "70%",
    color: "#fff",
    whiteSpace: "pre-wrap",
  },
  inputContainer: {
    display: "flex",
    marginTop: 10,
  },
  input: {
    flex: 1,
    padding: 10,
    fontSize: 16,
    border: "1px solid #555",
    borderRadius: 4,
    backgroundColor: "#222",
    color: "#fff",
  },
  sendButton: {
    padding: "0 20px",
    fontSize: 16,
    marginLeft: 8,
    backgroundColor: "#43a047",
    color: "#fff",
    border: "none",
    borderRadius: 4,
    cursor: "pointer",
  },
};

export default ChatInterface;
