document.addEventListener("DOMContentLoaded", () => {
  const boton = document.getElementById("btn-escanear-total");
  if (boton) {
    boton.addEventListener("click", async () => {
      try {
        const res = await fetch("http://localhost:5000/utilidades/escanear-total");
        const data = await res.json();

        if (data.status === "cambios_detectados") {
          alert("✅ Cambios detectados y aprendidos.");
        } else if (data.status === "sin_cambios") {
          alert("ℹ️ Sin cambios nuevos en el sistema.");
        } else {
          alert("⚠️ Resultado inesperado.");
        }

        console.log("Resultado del escaneo total:", data);
      } catch (error) {
        alert("❌ Error al escanear el sistema.");
        console.error(error);
      }
    });
  } else {
    console.warn("El botón con ID 'btn-escanear-total' no existe en el DOM.");
  }
});
